import axios from 'axios';
import { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import $ from 'jquery';
import Swal from "sweetalert2";

const url = "http://localhost:5000";
function AddLocPage() {
    const [images, setImages] = useState([]);
    const [detailpreview, setDetailImgs] = useState([]);
    

    const addImage = e => {
        const nowSelectImageList = e.target.files;
        let fileURL = [];

        let file;
        let maxFile = 50;
        let filesLength = nowSelectImageList.length > maxFile ? maxFile : nowSelectImageList.length;
        
        fileURL.push(nowSelectImageList);
        setImages(fileURL);
        for(var i=0; i<filesLength; i++){
            file = nowSelectImageList[i];
            if(file.type !== "image/jpeg" && file.type !== "image/jpg" && file.type !== "image/png"){
                setImages([]);
                Swal.fire({
                    icon: "warning",
                    title: "사진 형식 에러",
                    text: `JPG 사진 파일만 가능합니다.`,
                    showCancelButton: true,
                    confirmButtonText: "확인",
                    cancelButtonText: "취소",
                })
                break;
            } else{
                const preview = () => {
                    let file = images[i];
                    if(!images) return false;
            
                    const imgEl = document.querySelector('.img__preview');
                    const reader = new FileReader();
                    reader.onload = () => {
                        fileURL[i] = reader.result;
                        setDetailImgs([...fileURL]);
                        imgEl.style.backgroundImage = `url(${reader.result})`;
                    }
                    reader.readAsDataURL(detailpreview);
                    console.log(reader);
                }   
                preview()
                return () => preview();
            }
        }
    }
    
    const onClickHandler = () => {
        axios.post(url + '/api/upload/images', images, {
        })
        .then((res) => {
            if (res.data) {
                alert(res.data.msg);
            } else {
                alert(res.data.msg);
            }
        })
        .catch((err) => {
            alert("전송 실패 : " + err);
        });
    }
    axios.get(url + "/api/location/types", {
        headers: {
            "Content-Type": `application/json`,
        }
    }).then((res) => {
        var data = res.data;
        var innerHTML = "<option value=''>위치 타입 선택</option>";
        for(var i=0; i<data.length; i++){
            innerHTML += "<option value='" + data[i].loctype_id + "'>" + data[i].loctype_name + "</option>";
        }
        $(".selectbox").html(innerHTML);
    })

    useEffect(()=>{
        
    })
    
  return (
    <div className='m-5'>
    <Form name="form" method="post" encType='multipart/form-data' action="http://localhost:5000/api/upload/images">
        <Form.Label>위치 타입</Form.Label>
        <Form.Select aria-label="Default select example" className='selectbox'>
        <option value="1">One</option>
        <option value="2">Two</option>
        <option value="3">Three</option>
        </Form.Select>
        <br/>
        <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>위치명</Form.Label>
            <Form.Control type="text" placeholder="위치명입력" />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>위치 포인트</Form.Label>
            <Form.Control type="text" placeholder="위치 포인트 입력" />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword" onChange={ addImage }>
            <Form.Label>위치 사진</Form.Label>
            <Form.Control type="file" name="uploadfile" multiple="multiple" placeholder="위치 포인트 입력" />
        </Form.Group>
        <div className='img__preview' style={{width:'auto', height: '300px', backgroundRepeat: 'no-repeat'}}></div>
        <Button variant="primary" type="submit">
            Submit
        </Button>
    </Form>
    </div>
  );
}

export default AddLocPage;
