import React, { Component } from "react";
// import { Wrapper, Status } from "@googlemaps/react-wrapper";
import { GoogleMap, LoadScript } from "@react-google-maps/api";
import axios from 'axios';
import { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import $ from 'jquery';

const containerStyle = {
    width: '100%',
    height: '80vh'
};
  
const center = {
  lat: 35.118620223842,
  lng: 129.0959130382
};
const MapPage = () => {
  const url = "http://localhost:3000";
    axios.get(url + "/api/location/types", {
        headers: {
            "Content-Type": `application/json`,
        }
    }).then((res) => {
        var data = res.data;
        var innerHTML = "<option value='" + data[0].loctype_id + "'>위치 타입 선택</option>";
        for(var i=0; i<data.length; i++){
            innerHTML += "<option key={uuid()} value='" + data[i].loctype_id + "'>" + data[i].loctype_name + "</option>";
        }
        $(".selectbox").html(innerHTML);
    })
    
    return (
      <Container>
        <Row>
          <Col>
            <LoadScript googleMapsApiKey="AIzaSyB-RafIQqKjj2kactuwVHkuoulZSKA4rSs">
              <GoogleMap
                bootstrapURLKeys={{key:"AIzaSyB-RafIQqKjj2kactuwVHkuoulZSKA4rSs"}}
                mapContainerStyle={containerStyle}
                center={center}
                zoom={14}
              >
                { /* Child components, such as markers, info windows, etc. */ }
                <></>
              </GoogleMap>
            </LoadScript>
          </Col>
          <Col>2 of 2</Col>
        </Row>
      </Container>
      
              
    );
    
}

  
export default MapPage;
