import React, { Component } from "react";
// import { Wrapper, Status } from "@googlemaps/react-wrapper";
import { GoogleMap, LoadScript } from "@react-google-maps/api";
import axios from 'axios';
import { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image';
import Figure from 'react-bootstrap/Figure';
import $ from 'jquery';
import './Maps.css';
const containerStyle = {
    width: '100%',
    height: '80vh'
};

const imageStyle = {
  width: '20%',
  height: '80vh'
}
  
const center = {
  lat: 35.118620223842,
  lng: 129.0959130382
};
const MapPage = () => {
    const url = "http://travelbusanko.com";
    let loc_id = "LOC003";
    
    axios.get(url + `/api/view/image/${loc_id}`, {
        headers: {
            "Content-Type": `application/json`,
        }
    }).then((res) => {
        var data = res.data;
        var innerHTML = "";
        for(var i=0; i<data.length; i++){
          innerHTML += "<img src='http://travelbusanko.com/" + data[i].saved_path + "' class='image__box' />" ;
          
          // innerHTML += "<Figure src='http://travelbusanko.com/" + data[i].saved_path + "'/>";
        }
        console.log(innerHTML);
        $('.image__viewer').html(innerHTML);

    })
    
    return (
      <Container>
        <Row>
          <Col>
            <LoadScript googleMapsApiKey="AIzaSyB-RafIQqKjj2kactuwVHkuoulZSKA4rSs">
              <GoogleMap
                bootstrapURLKeys={{key:"AIzaSyB-RafIQqKjj2kactuwVHkuoulZSKA4rSs"}}
                mapContainerStyle={containerStyle}
                center={center}
                zoom={14}
              >
                { /* Child components, such as markers, info windows, etc. */ }
                <></>
              </GoogleMap>
            </LoadScript>
          </Col>
          <Col>
          <Figure className="image__viewer m-3">

          </Figure>
            {/* <div className="image__viewer m-3">
            
            </div> */}
          </Col>
        </Row>
      </Container>
      
              
    );
    
}

  
export default MapPage;
